import React, { useState } from "react";
import NewTodo from "./NewTodo";

const DummyToDo = [
  {
    id: 1,
    title: "Task 1",
    createdBy: "People 1",
  },
  {
    id: 2,
    title: "Task 2",
    createdBy: "People 2",
  },
  {
    id: 3,
    title: "Task 3",
    createdBy: "People 3",
  },
  {
    id: 4,
    title: "Task 4",
    createdBy: "People 4",
  },
  {
    id: 5,
    title: "Task 5",
    createdBy: "People 5",
  },
];

const TodoList = () => {
  const [toDoList, setToDoList] = useState(DummyToDo);

  const deleteButtonClickHandler = (id) => {
    console.log(id);
    const newToDoList = [...DummyToDo];
    const updatedToDo = newToDoList.filter((todo) => todo.id !== id);
    console.log(updatedToDo);
    setToDoList(updatedToDo);
  };

  const newToDoCreated = (todo) => {
    let newToDoList = [...DummyToDo];
    newToDoList.push(todo);
    setToDoList(newToDoList);
  };

  return (
    <div>
      <NewTodo newToDoCreated={newToDoCreated} />
      {toDoList.map((todo) => (
        <div
          key={todo.id}
          style={{ color: "blue", width: "70%", margin: "5px", padding: "2px" }}
        >
          <span>{todo.id}</span>
          <span>{todo.title}</span>
          <span>{todo.createdBy}</span>
          <button onClick={() => deleteButtonClickHandler(todo.id)}>
            Delete
          </button>
        </div>
      ))}
    </div>
  );
};

export default TodoList;
