import React, { useState } from "react";

const EditPage = (props) => {
  const toDo = props.editedTodo;
  const title = toDo.title;
  const createdBy = toDo.createdBy;
  console.log(toDo);
  const [newTitle, setNewTitle] = useState(title ? "" : title);
  const [newCreatedBy, setNewCreatedBy] = useState(createdBy ? "" : createdBy);

  const newEditedTitleChangeHandler = (e) => {
    e.preventDefault();
    setNewTitle(e.target.value);
  };
  const newEditedCreatedByChangeHandler = (e) => {
    e.preventDefault();
    setNewCreatedBy(e.target.value);
  };

  const updateButtonClickHandler = (e) => {
    e.preventDefault();
    const updatedToDo = {
      id: toDo.id,
      title: newTitle,
      createdBy: newCreatedBy,
    };
    props.newToDoCreated(updatedToDo);
    console.log(updatedToDo);
    setNewTitle("");
    setNewCreatedBy("");
  };

  return (
    <div>
      <label htmlFor="newTitle">New Title</label>
      <input
        id="newTitle"
        onChange={newEditedTitleChangeHandler}
        value={newTitle}
      ></input>
      <br />
      <label htmlFor="newcreatedby">New CreatedBy</label>
      <input
        id="newcreatedby"
        onChange={newEditedCreatedByChangeHandler}
        value={newCreatedBy}
      ></input>
      <button type="submit" onClick={updateButtonClickHandler}>
        Update
      </button>
    </div>
  );
};

export default EditPage;
