import React, { useState } from "react";

const EditPage = (props) => {
  const [newTitle, setNewTitle] = useState(props.editedTodo.title);
  const [newCreatedBy, setNewCreatedBy] = useState(props.editedTodo.createdBy);
  const toDo = props.editedTodo;
  console.log(toDo);

  const newEditedTitleChangeHandler = (e) => {
    e.preventDefault();
    setNewTitle(e.target.value);
  };
  const newEditedCreatedByChangeHandler = (e) => {
    e.preventDefault();
    setNewCreatedBy(e.target.value);
  };
  //   const submitButtonClickHandler = (e) => {
  //     e.preventDefault();
  //     const newTodo = {
  //       id: Math.floor(1000 * Math.random()),
  //       title: title,
  //       createdBy: createdBy,
  //     };
  //     props.newToDoCreated(newTodo);
  //     console.log(newTodo);
  //     setTitle("");
  //     setCreatedBy("");
  //   };

  return (
    <div>
      <label htmlFor="title">New Title</label>
      <input
        id="title"
        onChange={newEditedTitleChangeHandler}
        value={newTitle}
      ></input>
      <br />
      <label htmlFor="createdby">New CreatedBy</label>
      <input
        id="createdby"
        onChange={newEditedCreatedByChangeHandler}
        value={newCreatedBy}
      ></input>
      {/* <button type="submit" onClick={submitButtonClickHandler}>
        Submit
      </button> */}
    </div>
  );
};

export default EditPage;
